	_currentDate: function(){
		    var currentDate = new Date(),
            day = currentDate.getDate(),
            month = currentDate.getMonth() + 1;
            if (day < 10) {
                day = '0' + day;
            }
            if (month < 10) {
                month = '0' + month;
            }
            year = currentDate.getFullYear();
            date = year + "-" + month + "-" + day;
			return date;
	},
    _sendmail: function(body, ad_mail, us_mail, mail_sub,anonymous_sessions, maplink) {
		  var that = L.Control.MedborgarDialog.prototype;
          var body_content = encodeURIComponent(body);
          var admin_mail = encodeURIComponent(ad_mail);
          var user_mail = encodeURIComponent(us_mail);
          var mail_subject = encodeURIComponent(mail_sub);
          var maplink = encodeURIComponent(maplink);		  
		  var post_method = medborgerdialogSettings.app.trafikplan.layers.medborgerdialog_trafikplan_p.options.editOptions.post_method;				
		  var base_url = medborgerdialogSettings.app.trafikplan.layers.medborgerdialog_trafikplan_p.options.editOptions.sending_email.mail_service;
          var synpunkt_email_url = base_url + "?" + "&admin_adress="+admin_mail+"&receiver_address="+user_mail+"&subject="+mail_subject+"&body="+body_content+"&maplink="+maplink+"&"+that._randomTextAndNumberParameter() + "&session=" + anonymous_sessions + ".session";		  
		  try {
			  var synpunkt_email_xhr= new that._xhr_handler();
	             synpunkt_email_xhr.open(post_method, synpunkt_email_url, false);
	             synpunkt_email_xhr.send(null );			  
			  if (synpunkt_email_xhr.readyState == 4 && synpunkt_email_xhr.status == 200) {
			  var synpunkt_email_xhrResponse = JSON.parse(synpunkt_email_xhr.responseText); 
			  if(synpunkt_email_xhrResponse.send_status==="pass"){
                   return "true"; 
					}else{
						return "false"; 
					}					 
				 }
			   
				  
			  }catch(e){
				return "false";  
				  
			  }
			 			 
	},
	_create_remove_anonymous_sessions: function(tou_rm,session){
		
		  var that = L.Control.MedborgarDialog.prototype;	  
		  var post_method = medborgerdialogSettings.app.trafikplan.layers.medborgerdialog_trafikplan_p.options.editOptions.post_method;
          if(tou_rm === "create"){
			  var base_url = medborgerdialogSettings.app.trafikplan.layers.medborgerdialog_trafikplan_p.options.editOptions.med_create_anonymous_sessions  + "?" + that._randomTextAndNumberParameter() ;
		  }else if(tou_rm === "remove"){
			  var base_url = medborgerdialogSettings.app.trafikplan.layers.medborgerdialog_trafikplan_p.options.editOptions.med_remove_anonymous_sessions + "?" + "&session=" + session + ".session" +that._randomTextAndNumberParameter() ;
		  }		  
          var createRemoveSessionUrl = base_url;			  			  
		  try {
			  var createRemoveSessionXhr= new that._xhr_handler();
	             createRemoveSessionXhr.open(post_method, createRemoveSessionUrl, false);
	             createRemoveSessionXhr.send(null );			  
			  if (createRemoveSessionXhr.readyState == 4 && createRemoveSessionXhr.status == 200) {
			  var createRemoveSessionXhrResponse = JSON.parse(createRemoveSessionXhr.responseText); 
			  if(createRemoveSessionXhrResponse.session_status==="yes"){				  
                   return createRemoveSessionXhrResponse; 
					}else{
						return "false"; 
					}					 
				 }			   				  
			  }catch(e){
				return "false";  				  
			  }		
	}		
